
public class EjemploDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Lo hace minimo una vez, en este caso numero mayor que 5
		
	int numero = 8;
		
		do{
			System.out.println("Hola " + numero);
			numero++;
		}while(numero <= 5);

	}

}
