package For;

public class Ejercicio4 {
	
	//Escribir el factorial del numero 15

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double fact = 1;
		
	        for (int i = 15; i > 0; i--) {
	            fact = fact * i;
	        }
	        System.out.println(fact);

	}

}
