import java.util.Vector;

public class PrincipalVector {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] matriz = new int[10]; 
		 matriz[0] = 10; 
		 matriz[1] = 20; 
		 matriz[2] = 30; 
		 System.out.println( "Longitud de la matriz: " + matriz.length); 
		 
		 // Coleccion
		 Vector<Integer> coleccion = new Vector<Integer>(); 
		 coleccion.add(10); 
		 coleccion.add(20); 
		 coleccion.add(30); 
		 System.out.println( "Longitud de la coleccion: " + coleccion.size()); 
		 } 
	
	}


