// Crea una función que dada una matriz de enteros retorna su longitud// Crea una función que dada una matriz de enteros retorna su longitud

public class Ejer1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[]m=new int[]{5,4,8,9,6};
		System.out.println(longitud(m));;

	}
	
	static int longitud(int[]matriz){
		int longitud = 0;
		for (int i = 0; i < matriz.length; i++) {
			longitud = matriz.length;
		}
		return longitud;
	}

}
